sources.styles\\manage-budget.scss:
.manage-budget {
  padding: 15px 0;
}

.manage-budget-content {
  justify-content: center;

  &--upper {
    display: flex;
    margin-bottom: 10px;
  }

  &--lower {
    display: flex;
    width: 100%;
  }
}

.dropdown, .input {
  //text-align: center;
  background-color: #FFFFFF;
  background-image: none;
  border: 0px solid transparent;
  border-radius: 4px;
  box-shadow: 0 1px 1px rgba(0, 0, 0, 0.075) inset;
  color: $black;
  dispjlay: block;
  font-size: 18px;
  padding: 20px 12px;
  transition: border-color 0.15s ease-in-out 0s, box-shadow 0.15s ease-in-out 0s;

  &--category {
    margin: 0 10px;
  }

  &--description {
    width: 260px;
    margin-right: 10px;
    padding-left: 20px;
    transition: background-color 0.3s ease;
    transition: color 0.3s ease;
  }

  &--value {
    width: 70px;
    padding-left: 20px;
    margin: 0;
  }

  &::placeholder {
    font-family: $mainFont;
    color: grey;
  }

  &:focus {
    border-color: #66afe9;
    outline: 0;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(102, 175, 233, .6);
  }
}

.btn-submit {
  background-color: #E8DC00;
}

input, select {
  border: 1px solid transparent !important;
  &.error {
  color: #FF0000;
    //background-color: rgba(#FF0000,0.1);
    border-color: #FF0000 !important ;

  }
}

