.statistics {
  padding: 13px 0;
  width: 100%;
}
.main {
  flex: 0 0 60%;
}

.statistics-content {
  display: flex;
  justify-content: center;
  color: black;
 //width: 40%;

}

.statistics-box {
  border: #8489A3 solid 1px;
  padding: 16px;
  margin: 0 10px;
  min-height: 250px;
  width: 45%;
  box-shadow: 0px 1px 3px 0px rgba(0,0,0,0.2), 0px 1px 1px 0px rgba(0,0,0,0.14), 0px 2px 1px -1px rgba(0,0,0,0.12);
  border-radius: 4px;
}
.categories-header{
  display: flex;
  justify-content: space-between;
  align-items: baseline;
  select {
    font: $mainFont;
    color: $purple;
    padding-top: 0;
    padding-left: 20px;
    -webkit-appearance: none;
    -moz-appearance: none;
    text-indent: 1px;
    text-overflow: '';
  }
}
.statistics-header {
  font-size: 30px;
  margin-bottom: 15px;

  &--income {
    color: rgb(232, 220, 0);
  }

  &--expense {
    color: gray;
  }
}

.statistics-list {
  font-size: 25px;
  color: black;
}

.income-item, .expense-item {
  margin-bottom: 6px;
  padding: 10px 5px;
background-color: #fff;
  border-radius: 5px;
  transition: 3s;
  justify-content: space-between;
  align-items: center;
  border-bottom: 1px solid $lightgray;
}
.item-details{
  display: flex;
  justify-content: space-between;
  align-items: center;
}
.span-description {
  padding-right: 30px;
  padding-bottom: 10px;
  font-size: 15px;

}
.span-category {
  padding-right: 30px;
  padding-bottom: 10px;
}
.span-value {
  padding-left: 30px;
  padding-bottom: 10px;
  max-width: 300px;
}
// time
.time-container {
display: flex;
  text-align:right;
  justify-content: flex-end;
  span{
    //display:block
  }
}
.span-time{
text-align: right;
font-size: 15px;
  padding-right: 20px;
}
.btn-delete {
background-image: url("img/cross.png");
  background-size: cover;
  margin-left: 10px;
  width: 15px;
  height: 15px;
  border: none;
  cursor: pointer;
  text-align: center;

  &:hover {
    border: none;
    background-color: $lightgray;
    color: $black;
  }
}
.cross {
  position: absolute;
  right: 32px;
  top: 32px;
  width: 32px;
  height: 32px;
  opacity: 0.3;
}
.close:hover {
  opacity: 1;
}
.cross:before, .cross:after {
  position: absolute;
  left: 15px;
  content: ' ';
  height: 33px;
  width: 2px;
  background-color: #333;
}
.cross:before {
  transform: rotate(45deg);
}
.cross:after {
  transform: rotate(-45deg);
}
